```csharp  
using System;  
using System.Globalization;  
using System.Speech.Recognition;  
using System.Threading;  

namespace SreEmulateRecognizeAsync  
{  
  class Program  
  {  
    // Indicate when an asynchronous operation is finished.  
    static bool completed;  

    static void Main(string[] args)  
    {  
      using (SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(new CultureInfo("en-US")))  
      {  
        // Load grammars.  
        recognizer.LoadGrammar(CreateNameGrammar("Smith"));  
        recognizer.LoadGrammar(CreateNameGrammar("Jones"));  

        // Configure the audio input.  
        recognizer.SetInputToNull();  

        // Add event handlers for the events raised by the  
        // EmulateRecognizeAsync method.  
        recognizer.SpeechDetected +=  
          new EventHandler<SpeechDetectedEventArgs>(  
            SpeechDetectedHandler);  
        recognizer.SpeechHypothesized +=  
          new EventHandler<SpeechHypothesizedEventArgs>(  
            SpeechHypothesizedHandler);  
        recognizer.SpeechRecognitionRejected +=  
          new EventHandler<SpeechRecognitionRejectedEventArgs>(  
            SpeechRecognitionRejectedHandler);  
        recognizer.SpeechRecognized +=  
          new EventHandler<SpeechRecognizedEventArgs>(  
            SpeechRecognizedHandler);  
        recognizer.EmulateRecognizeCompleted +=  
          new EventHandler<EmulateRecognizeCompletedEventArgs>(  
            EmulateRecognizeCompletedHander);  

        // Start four asynchronous emulated recognition operations.  
        TestRecognizeAsync(recognizer, "Smith");  
        TestRecognizeAsync(recognizer, "Jones");  
        TestRecognizeAsync(recognizer, "Mister");  
        TestRecognizeAsync(recognizer, "Mister Smith");  
      }  

      Console.WriteLine("press any key to exit...");  
      Console.ReadKey(true);  
    }  

    // Create a simple name grammar.  
    // Set the grammar name to the surname.  
    private static Grammar CreateNameGrammar(string surname)  
    {  
      GrammarBuilder builder = new GrammarBuilder("mister", 0, 1);  
      builder.Append(surname);  

      Grammar nameGrammar = new Grammar(builder);  
      nameGrammar.Name = surname;  

      return nameGrammar;  
    }  

    // Send emulated input to the recognizer for asynchronous  
    // recognition.  
    private static void TestRecognizeAsync(  
      SpeechRecognitionEngine recognizer, string input)  
    {  
      completed = false;  

      Console.WriteLine("TestRecognizeAsync(\"{0}\")...", input);  
      recognizer.EmulateRecognizeAsync(input);  

      // Wait for the operation to complete.  
      while (!completed)  
      {  
        Thread.Sleep(333);  
      }  

      Console.WriteLine(" Done.");  
      Console.WriteLine();  
    }  

    static void SpeechDetectedHandler(  
      object sender, SpeechDetectedEventArgs e)  
    {  
      Console.WriteLine(" SpeechDetected event raised.");  
    }  

    static void SpeechHypothesizedHandler(  
      object sender, SpeechHypothesizedEventArgs e)  
    {  
      Console.WriteLine(" SpeechHypothesized event raised.");  
      if (e.Result != null)  
      {  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          e.Result.Grammar.Name ?? "<none>", e.Result.Text);  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
    }  

    // Handle events.  
    static void SpeechRecognitionRejectedHandler(  
      object sender, SpeechRecognitionRejectedEventArgs e)  
    {  
      Console.WriteLine(" SpeechRecognitionRejected event raised.");  
      if (e.Result != null)  
      {  
        string grammarName;  
        if (e.Result.Grammar != null)  
        {  
          grammarName = e.Result.Grammar.Name ?? "<none>";  
        }  
        else  
        {  
          grammarName = "<not available>";  
        }  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          grammarName, e.Result.Text);  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
    }  

    static void SpeechRecognizedHandler(  
      object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine(" SpeechRecognized event raised.");  
      if (e.Result != null)  
      {  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          e.Result.Grammar.Name ?? "<none>", e.Result.Text );  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  
    }  

    static void EmulateRecognizeCompletedHander(  
      object sender, EmulateRecognizeCompletedEventArgs e)  
    {  
      Console.WriteLine(" EmulateRecognizeCompleted event raised.");  

      if (e.Error != null)  
      {  
        Console.WriteLine("  {0} exception encountered: {1}:",  
          e.Error.GetType().Name, e.Error.Message);  
      }  
      else if (e.Cancelled)  
      {  
        Console.WriteLine("  Operation cancelled.");  
      }  
      else if (e.Result != null)  
      {  
        Console.WriteLine("  Grammar = {0}; Text = {1}",  
          e.Result.Grammar.Name ?? "<none>", e.Result.Text);  
      }  
      else  
      {  
        Console.WriteLine("  No recognition result available.");  
      }  

      completed = true;  
    }  
  }  
}  

```



## Remarks  
 <span data-ttu-id="0abba-342">Каждый <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> метод начинает операцию асинхронного распознавания.</span><span class="sxs-lookup"><span data-stu-id="0abba-342">Each <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> method begins an asynchronous recognition operation.</span></span> <span data-ttu-id="0abba-343"><xref:System.Speech.Recognition.SpeechRecognitionEngine> Вызывает <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> событие, когда он завершает асинхронную операцию.</span><span class="sxs-lookup"><span data-stu-id="0abba-343">The <xref:System.Speech.Recognition.SpeechRecognitionEngine> raises the <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> event when it finalizes the asynchronous operation.</span></span>  

 <span data-ttu-id="0abba-344"><xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> Операции могут вызывать <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechDetected>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechHypothesized>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognitionRejected>, и <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> события.</span><span class="sxs-lookup"><span data-stu-id="0abba-344">The <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeAsync%2A> operation can raise the <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechDetected>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechHypothesized>, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognitionRejected>, and <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> events.</span></span> <span data-ttu-id="0abba-345"><xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> Событий — последний такого события, что распознаватель создает для данной операции.</span><span class="sxs-lookup"><span data-stu-id="0abba-345">The <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> event is the last such event that the recognizer raises for a given operation.</span></span>  

 <span data-ttu-id="0abba-346">При успешном выполнении эмулированной распознавания, вы можете использовать результат распознавания, используя одно из следующих:</span><span class="sxs-lookup"><span data-stu-id="0abba-346">If emulated recognition was successful, you can access the recognition result using the either of the following:</span></span>  

-   <span data-ttu-id="0abba-347"><xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> Свойство в <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> объекта в обработчике <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> событий.</span><span class="sxs-lookup"><span data-stu-id="0abba-347">The <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> property in the <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> object in the handler for the <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> event.</span></span>  

-   <span data-ttu-id="0abba-348"><xref:System.Speech.Recognition.RecognitionEventArgs.Result%2A> свойство в <xref:System.Speech.Recognition.SpeechRecognizedEventArgs> объекта в обработчике <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> событий.</span><span class="sxs-lookup"><span data-stu-id="0abba-348"><xref:System.Speech.Recognition.RecognitionEventArgs.Result%2A> property in the <xref:System.Speech.Recognition.SpeechRecognizedEventArgs> object in the handler for the <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> event.</span></span>  

 <span data-ttu-id="0abba-349">Если эмулированной распознавания не выполнена, <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> событие не происходит и <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> будет иметь значение null.</span><span class="sxs-lookup"><span data-stu-id="0abba-349">If emulated recognition was not successful, the <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> event is not raised and the <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs.Result%2A> will be null.</span></span>  

 <span data-ttu-id="0abba-350">Интерфейс <xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> является производным от интерфейса <xref:System.ComponentModel.AsyncCompletedEventArgs>.</span><span class="sxs-lookup"><span data-stu-id="0abba-350"><xref:System.Speech.Recognition.EmulateRecognizeCompletedEventArgs> derives from <xref:System.ComponentModel.AsyncCompletedEventArgs>.</span></span>  

 <span data-ttu-id="0abba-351">Интерфейс <xref:System.Speech.Recognition.SpeechRecognizedEventArgs> является производным от интерфейса <xref:System.Speech.Recognition.RecognitionEventArgs>.</span><span class="sxs-lookup"><span data-stu-id="0abba-351"><xref:System.Speech.Recognition.SpeechRecognizedEventArgs> derives from <xref:System.Speech.Recognition.RecognitionEventArgs>.</span></span>  

 <span data-ttu-id="0abba-352">При создании делегата <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> определяется метод обработки события.</span><span class="sxs-lookup"><span data-stu-id="0abba-352">When you create an <xref:System.Speech.Recognition.SpeechRecognitionEngine.EmulateRecognizeCompleted> delegate, you identify the method that will handle the event.</span></span> <span data-ttu-id="0abba-353">Чтобы связать событие с обработчиком событий, нужно добавить в событие экземпляр делегата.</span><span class="sxs-lookup"><span data-stu-id="0abba-353">To associate the event with your event handler, add an instance of the delegate to the event.</span></span> <span data-ttu-id="0abba-354">Обработчик событий вызывается всякий раз, когда происходит событие, если делегат не удален.</span><span class="sxs-lookup"><span data-stu-id="0abba-354">The event handler is called whenever the event occurs, unless you remove the delegate.</span></span> <span data-ttu-id="0abba-355">Дополнительные сведения о делегатах-обработчиках событий см. в разделе [события и делегаты](http://go.microsoft.com/fwlink/?LinkId=162418).</span><span class="sxs-lookup"><span data-stu-id="0abba-355">For more information about event-handler delegates, see [Events and Delegates](http://go.microsoft.com/fwlink/?LinkId=162418).</span></span>  



## Examples  
 <span data-ttu-id="0abba-356">Следующий пример является частью консольное приложение, которое загружает Грамматика распознавания речи и демонстрирует асинхронные эмулированной входных данных, результаты распознавания связанные и связанные события, вызываемые распознаватель речи.</span><span class="sxs-lookup"><span data-stu-id="0abba-356">The following example is part of a console application that loads a speech recognition grammar and demonstrates asynchronous emulated input, the associated recognition results, and the associated events raised by the speech recognizer.</span></span>  

```  
using System;  
using System.Speech.Recognition;  
using System.Threading;  

namespace InProcessRecognizer  
{  
  class Program  
  {  
    // Indicate whether the asynchronous emulate recognition  
    // operation has completed.  
    static bool completed;  

    static void Main(string[] args)  
    {  

      // Initialize an instance of an in-process recognizer.  
      using (SpeechRecognitionEngine recognizer =   
        new SpeechRecognitionEngine(new System.Globalization.CultureInfo("en-US")))  
      {  
        // Create and load a sample grammar.  
        Grammar testGrammar =  
          new Grammar(new GrammarBuilder("testing testing"));  
        testGrammar.Name = "Test Grammar";  
        recognizer.LoadGrammar(testGrammar);  

        // Attach event handlers for recognition events.  
        recognizer.SpeechRecognized +=  
          new EventHandler<SpeechRecognizedEventArgs>(SpeechRecognizedHandler);  
        recognizer.EmulateRecognizeCompleted +=  
          new EventHandler<EmulateRecognizeCompletedEventArgs>(  
            EmulateRecognizeCompletedHandler);  

        completed = false;  

        // This EmulateRecognizeAsync call mathches the grammar  
        // and generates a SpeechRecognized event.  
        recognizer.EmulateRecognizeAsync("testing testing");  

        // Wait for the asynchronous operation to complete.  
        while (!completed)  
        {  
          Thread.Sleep(333);  
        }  

        completed = false;  

        // This EmulateRecognizeAsync call does not match the grammar  
        // or generate a SpeechRecognized event.  
        recognizer.EmulateRecognizeAsync("testing one two three");  

        // Wait for the asynchronous operation to complete.  
        while (!completed)  
        {  
          Thread.Sleep(333);  
        }  
      }  

      Console.WriteLine();  
      Console.WriteLine("Press any key to exit...");  
      Console.ReadKey();  
    }  

    // Handle the SpeechRecognized event.  
    static void SpeechRecognizedHandler(  
      object sender, SpeechRecognizedEventArgs e)  
    {  
      if (e.Result != null)  
      {  
        Console.WriteLine("Result of 1st call to EmulateRecognizeAsync = {0}",  
          e.Result.Text ?? "<no text>");  
        Console.WriteLine();  
      }  
      else  
      {  
        Console.WriteLine("No recognition result");  
      }  
    }  

    // Handle the EmulateRecognizeCompleted event.  
    static void EmulateRecognizeCompletedHandler(  
      object sender, EmulateRecognizeCompletedEventArgs e)  
    {  
      if (e.Result == null)  
      {  
        Console.WriteLine("Result of 2nd call to EmulateRecognizeAsync = No result generated.");  
      }  

      // Indicate the asynchronous operation is complete.  
      completed = true;  
    }  
  }  
}  

```



## Examples  
 <span data-ttu-id="0abba-377">В следующем примере выводится информация для каждого грамматики распознавания речи, в данный момент загружен распознавателем речи на консоль.</span><span class="sxs-lookup"><span data-stu-id="0abba-377">The following example outputs information to the console for each speech recognition grammar that is currently loaded by a speech recognizer.</span></span>  

> [!IMPORTANT]
>  <span data-ttu-id="0abba-378">Скопируйте грамматики коллекции, чтобы избежать ошибок, если коллекция была изменена, пока этот метод выполняет перечисление элементов коллекции.</span><span class="sxs-lookup"><span data-stu-id="0abba-378">Copy the grammar collection to avoid errors if the collection is modified while this method enumerates the elements of the collection.</span></span>  

```csharp  

private static void ListGrammars(SpeechRecognitionEngine recognizer)  
{  
  string qualifier;  
  List<Grammar> grammars = new List<Grammar>(recognizer.Grammars);  
  foreach (Grammar g in grammars)  
  {  
    qualifier = (g.Enabled) ? "enabled" : "disabled";  

    Console.WriteLine("Grammar {0} is loaded and is {1}.",  
      g.Name, qualifier);  
  }  
}  
```



## Remarks  
 <span data-ttu-id="0abba-381">Каждый распознаватель речи использует алгоритм для различения бездействия и речи.</span><span class="sxs-lookup"><span data-stu-id="0abba-381">Each speech recognizer has an algorithm to distinguish between silence and speech.</span></span> <span data-ttu-id="0abba-382">Если вход распознаватель бездействия во время начального времени ожидания бездействия, распознаватель завершает операции распознавания.</span><span class="sxs-lookup"><span data-stu-id="0abba-382">If the recognizer input is silence during the initial silence timeout period, then the recognizer finalizes that recognition operation.</span></span>  

-   <span data-ttu-id="0abba-383">Для распознавания асинхронных операций и эмуляции, вызывает распознаватель <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> событий, где <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.InitialSilenceTimeout%2A?displayProperty=nameWithType> свойство `true`и <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.Result%2A?displayProperty=nameWithType> свойство `null`.</span><span class="sxs-lookup"><span data-stu-id="0abba-383">For asynchronous recognition operations and emulation, the recognizer raises the <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> event, where the <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.InitialSilenceTimeout%2A?displayProperty=nameWithType> property is `true`, and the <xref:System.Speech.Recognition.RecognizeCompletedEventArgs.Result%2A?displayProperty=nameWithType> property is `null`.</span></span>  

-   <span data-ttu-id="0abba-384">Для распознавания синхронной операции и эмуляции, возвращает распознаватель `null`, а не является допустимым <xref:System.Speech.Recognition.RecognitionResult>.</span><span class="sxs-lookup"><span data-stu-id="0abba-384">For synchronous recognition operations and emulation, the recognizer returns `null`, instead of a valid <xref:System.Speech.Recognition.RecognitionResult>.</span></span>  

 <span data-ttu-id="0abba-385">Если интервал времени ожидания бездействия начальной имеет значение 0, распознаватель не выполняет проверку времени ожидания бездействия начальной.</span><span class="sxs-lookup"><span data-stu-id="0abba-385">If the initial silence timeout interval is set to 0, the recognizer does not perform an initial silence timeout check.</span></span> <span data-ttu-id="0abba-386">Интервал времени ожидания может быть любым положительным значением.</span><span class="sxs-lookup"><span data-stu-id="0abba-386">The timeout interval can be any non-negative value.</span></span> <span data-ttu-id="0abba-387">Значение по умолчанию — 0 секунд.</span><span class="sxs-lookup"><span data-stu-id="0abba-387">The default is 0 seconds.</span></span>  



## Examples  
 <span data-ttu-id="0abba-388">Приведенный ниже показана часть консольного приложения, демонстрирующий распознавания речи basic.</span><span class="sxs-lookup"><span data-stu-id="0abba-388">The following example shows part of a console application that demonstrates basic speech recognition.</span></span> <span data-ttu-id="0abba-389">В примере устанавливается <xref:System.Speech.Recognition.SpeechRecognitionEngine.BabbleTimeout%2A> и <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> свойства <xref:System.Speech.Recognition.SpeechRecognitionEngine> перед запуском распознавания речи.</span><span class="sxs-lookup"><span data-stu-id="0abba-389">The example sets the <xref:System.Speech.Recognition.SpeechRecognitionEngine.BabbleTimeout%2A> and <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> properties of a <xref:System.Speech.Recognition.SpeechRecognitionEngine> before initiating speech recognition.</span></span> <span data-ttu-id="0abba-390">Обработчики для распознавания речи <xref:System.Speech.Recognition.SpeechRecognitionEngine.AudioStateChanged> и <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> события выходные сведения о событии в консоль, чтобы продемонстрировать, как <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> свойства <xref:System.Speech.Recognition.SpeechRecognitionEngine> свойства влияют на операции распознавания.</span><span class="sxs-lookup"><span data-stu-id="0abba-390">Handlers for the speech recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.AudioStateChanged> and <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizeCompleted> events output event information to the console to demonstrate how the <xref:System.Speech.Recognition.SpeechRecognitionEngine.InitialSilenceTimeout%2A> properties of a <xref:System.Speech.Recognition.SpeechRecognitionEngine> properties affect recognition operations.</span></span>  

```csharp  

using System;  
using System.Speech.Recognition;  

namespace SpeechRecognitionApp  
{  
  class Program  
  {  
    static void Main(string[] args)  
    {  

      // Initialize an in-process speech recognizer.  
      using (SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(  
          new System.Globalization.CultureInfo("en-US")))  
      {  
        // Load a Grammar object.  
        recognizer.LoadGrammar(CreateServicesGrammar("FindServices"));  

        // Add event handlers.  
        recognizer.AudioStateChanged +=  
          new EventHandler<AudioStateChangedEventArgs>(  
            AudioStateChangedHandler);  
        recognizer.RecognizeCompleted +=  
          new EventHandler<RecognizeCompletedEventArgs>(  
            RecognizeCompletedHandler);  

        // Configure input to the speech recognizer.  
        recognizer.SetInputToDefaultAudioDevice();  

        recognizer.InitialSilenceTimeout = TimeSpan.FromSeconds(3);  
        recognizer.BabbleTimeout = TimeSpan.FromSeconds(2);  
        recognizer.EndSilenceTimeout = TimeSpan.FromSeconds(1);  
        recognizer.EndSilenceTimeoutAmbiguous = TimeSpan.FromSeconds(1.5);  

        Console.WriteLine("BabbleTimeout: {0}", recognizer.BabbleTimeout);  
        Console.WriteLine("InitialSilenceTimeout: {0}", recognizer.InitialSilenceTimeout);  
        Console.WriteLine("EndSilenceTimeout: {0}", recognizer.EndSilenceTimeout);  
        Console.WriteLine("EndSilenceTimeoutAmbiguous: {0}", recognizer.EndSilenceTimeoutAmbiguous);  
        Console.WriteLine();  

        // Start asynchronous speech recognition.  
        recognizer.RecognizeAsync(RecognizeMode.Single);  

        // Keep the console window open.  
        while (true)  
        {  
          Console.ReadLine();  
        }  
      }  
    }  

    // Create a grammar and build it into a Grammar object.   
    static Grammar CreateServicesGrammar(string grammarName)  
    {  

      // Create a grammar for finding services in different cities.  
      Choices services = new Choices(new string[] { "restaurants", "hotels", "gas stations" });  
      Choices cities = new Choices(new string[] { "Seattle", "Boston", "Dallas" });  

      GrammarBuilder findServices = new GrammarBuilder("Find");  
      findServices.Append(services);  
      findServices.Append("near");  
      findServices.Append(cities);  

      // Create a Grammar object from the GrammarBuilder..  
      Grammar servicesGrammar = new Grammar(findServices);  
      servicesGrammar.Name = ("FindServices");  
      return servicesGrammar;  
    }  

    // Handle the AudioStateChanged event.  
    static void AudioStateChangedHandler(  
      object sender, AudioStateChangedEventArgs e)  
    {  
      Console.WriteLine("AudioStateChanged ({0}): {1}",  
        DateTime.Now.ToString("mm:ss.f"), e.AudioState);  
    }  

    // Handle the RecognizeCompleted event.  
    static void RecognizeCompletedHandler(  
      object sender, RecognizeCompletedEventArgs e)  
    {  
      Console.WriteLine("RecognizeCompleted ({0}):",  
        DateTime.Now.ToString("mm:ss.f"));  

      string resultText;  
      if (e.Result != null) { resultText = e.Result.Text; }  
      else { resultText = "<null>"; }  

      Console.WriteLine(  
        " BabbleTimeout: {0}; InitialSilenceTimeout: {1}; Result text: {2}",  
        e.BabbleTimeout, e.InitialSilenceTimeout, resultText);  
      if (e.Error != null)  
      {  
        Console.WriteLine(" Exception message: ", e.Error.Message);  
      }  

      // Start the next asynchronous recognition operation.  
      ((SpeechRecognitionEngine)sender).RecognizeAsync(RecognizeMode.Single);  
    }  
  }  
}  

```



## Remarks  
 <span data-ttu-id="0abba-394">Чтобы получить сведения о текущем распознавателя, используйте <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizerInfo%2A> свойство.</span><span class="sxs-lookup"><span data-stu-id="0abba-394">To get information about the current recognizer, use the <xref:System.Speech.Recognition.SpeechRecognitionEngine.RecognizerInfo%2A> property.</span></span>  



## Examples  
 <span data-ttu-id="0abba-395">Приведенный ниже показана часть консольного приложения, демонстрирующий распознавания речи basic.</span><span class="sxs-lookup"><span data-stu-id="0abba-395">The following example shows part of a console application that demonstrates basic speech recognition.</span></span> <span data-ttu-id="0abba-396">В этом примере в коллекцию, возвращаемую <xref:System.Speech.Recognition.SpeechRecognitionEngine.InstalledRecognizers%2A> способ поиска распознавания речи, который поддерживает английский язык.</span><span class="sxs-lookup"><span data-stu-id="0abba-396">The example uses the collection returned by the <xref:System.Speech.Recognition.SpeechRecognitionEngine.InstalledRecognizers%2A> method to find a speech recognizer that supports the English language.</span></span>  

```csharp  
using System;  
using System.Speech.Recognition;  

namespace SpeechRecognitionApp  
{  
  class Program  
  {  
    static void Main(string[] args)  
    {  

      // Select a speech recognizer that supports English.  
      RecognizerInfo info = null;  
      foreach (RecognizerInfo ri in SpeechRecognitionEngine.InstalledRecognizers())  
      {  
        if (ri.Culture.TwoLetterISOLanguageName.Equals("en"))  
        {  
          info = ri;  
          break;  
        }  
      }  
      if (info == null) return;  

      // Create the selected recognizer.  
      using (SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(info))  
      {  

        // Create and load a dictation grammar.  
        recognizer.LoadGrammar(new DictationGrammar());  

        // Add a handler for the speech recognized event.  
        recognizer.SpeechRecognized +=   
          new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  

        // Configure input to the speech recognizer.  
        recognizer.SetInputToDefaultAudioDevice();  

        // Start asynchronous, continuous speech recognition.  
        recognizer.RecognizeAsync(RecognizeMode.Multiple);  

        // Keep the console window open.  
        while (true)  
        {  
          Console.ReadLine();  
        }  
      }  
    }  

    // Handle the SpeechRecognized event.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine("Recognized text: " + e.Result.Text);  
    }  
  }  
}  
```



## Remarks  
 <span data-ttu-id="0abba-399">Распознаватель вызывает исключение, если <xref:System.Speech.Recognition.Grammar> объекта уже загружен, загружается асинхронно или не удалось загрузить в любой распознаватель.</span><span class="sxs-lookup"><span data-stu-id="0abba-399">The recognizer throws an exception if the <xref:System.Speech.Recognition.Grammar> object is already loaded, is being asynchronously loaded, or has failed to load into any recognizer.</span></span> <span data-ttu-id="0abba-400">Не удается загрузить же <xref:System.Speech.Recognition.Grammar> объект в нескольких экземплярах <xref:System.Speech.Recognition.SpeechRecognitionEngine>.</span><span class="sxs-lookup"><span data-stu-id="0abba-400">You cannot load the same <xref:System.Speech.Recognition.Grammar> object into multiple instances of <xref:System.Speech.Recognition.SpeechRecognitionEngine>.</span></span> <span data-ttu-id="0abba-401">Вместо этого создайте новый <xref:System.Speech.Recognition.Grammar> объекта для каждого <xref:System.Speech.Recognition.SpeechRecognitionEngine> экземпляр.</span><span class="sxs-lookup"><span data-stu-id="0abba-401">Instead, create a new <xref:System.Speech.Recognition.Grammar> object for each <xref:System.Speech.Recognition.SpeechRecognitionEngine> instance.</span></span>  

 <span data-ttu-id="0abba-402">Если выполняется распознаватель, приложения должны использовать <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> Приостановка механизма распознавания речи перед загрузка, выгрузка, включение или отключение грамматика.</span><span class="sxs-lookup"><span data-stu-id="0abba-402">If the recognizer is running, applications must use <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> to pause the speech recognition engine before loading, unloading,  enabling, or disabling a grammar.</span></span>  

 <span data-ttu-id="0abba-403">При загрузке Грамматика оно включено по умолчанию.</span><span class="sxs-lookup"><span data-stu-id="0abba-403">When you load a grammar, it is enabled by default.</span></span> <span data-ttu-id="0abba-404">Чтобы отключить Грамматика загружена, используйте <xref:System.Speech.Recognition.Grammar.Enabled%2A> свойство.</span><span class="sxs-lookup"><span data-stu-id="0abba-404">To disable a loaded grammar, use the <xref:System.Speech.Recognition.Grammar.Enabled%2A> property.</span></span>  

 <span data-ttu-id="0abba-405">Чтобы загрузить <xref:System.Speech.Recognition.Grammar> асинхронно, используйте <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> метод.</span><span class="sxs-lookup"><span data-stu-id="0abba-405">To load a <xref:System.Speech.Recognition.Grammar> object asynchronously, use the <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> method.</span></span>  



## Examples  
 <span data-ttu-id="0abba-406">Приведенный ниже показана часть консольного приложения, демонстрирующий распознавания речи basic.</span><span class="sxs-lookup"><span data-stu-id="0abba-406">The following example shows part of a console application that demonstrates basic speech recognition.</span></span> <span data-ttu-id="0abba-407">В примере создается <xref:System.Speech.Recognition.DictationGrammar> и загружает их в распознавания речи.</span><span class="sxs-lookup"><span data-stu-id="0abba-407">The example creates a <xref:System.Speech.Recognition.DictationGrammar> and loads it into a speech recognizer.</span></span>  

```csharp  
using System;  
using System.Speech.Recognition;  

namespace SpeechRecognitionApp  
{  
  class Program  
  {  
    static void Main(string[] args)  
    {  

      // Create an in-process speech recognizer for the en-US locale.  
      using (  
      SpeechRecognitionEngine recognizer =  
        new SpeechRecognitionEngine(  
          new System.Globalization.CultureInfo("en-US")))  
      {  

        // Create and load a dictation grammar.  
        recognizer.LoadGrammar(new DictationGrammar());  

        // Add a handler for the speech recognized event.  
        recognizer.SpeechRecognized +=   
          new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  

        // Configure input to the speech recognizer.  
        recognizer.SetInputToDefaultAudioDevice();  

        // Start asynchronous, continuous speech recognition.  
        recognizer.RecognizeAsync(RecognizeMode.Multiple);  

        // Keep the console window open.  
        while (true)  
        {  
          Console.ReadLine();  
        }  
      }  
    }  

    // Handle the SpeechRecognized event.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine("Recognized text: " + e.Result.Text);  
    }  
  }  
}  

```



## Remarks  
 <span data-ttu-id="0abba-424">Распознаватель <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> метода запускает асинхронную операцию.</span><span class="sxs-lookup"><span data-stu-id="0abba-424">The recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarAsync%2A> method initiates an asynchronous operation.</span></span> <span data-ttu-id="0abba-425"><xref:System.Speech.Recognition.SpeechRecognitionEngine> Вызывает это событие, если в результате этой операции.</span><span class="sxs-lookup"><span data-stu-id="0abba-425">The <xref:System.Speech.Recognition.SpeechRecognitionEngine> raises this event when it completes the operation.</span></span> <span data-ttu-id="0abba-426">Для получения <xref:System.Speech.Recognition.Grammar> загрузку распознаватель, используйте <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs.Grammar%2A> свойства связанного <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs>.</span><span class="sxs-lookup"><span data-stu-id="0abba-426">To get the <xref:System.Speech.Recognition.Grammar> object that the recognizer loaded, use the <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs.Grammar%2A> property of the associated <xref:System.Speech.Recognition.LoadGrammarCompletedEventArgs>.</span></span> <span data-ttu-id="0abba-427">Для получения текущего <xref:System.Speech.Recognition.Grammar> объектов распознаватель были загружены, использовать распознаватель <xref:System.Speech.Recognition.SpeechRecognitionEngine.Grammars%2A> свойство.</span><span class="sxs-lookup"><span data-stu-id="0abba-427">To get the current <xref:System.Speech.Recognition.Grammar> objects the recognizer has loaded, use the recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.Grammars%2A> property.</span></span>  

 <span data-ttu-id="0abba-428">Если выполняется распознаватель, приложения должны использовать <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> Приостановка механизма распознавания речи перед загрузка, выгрузка, включение или отключение грамматика.</span><span class="sxs-lookup"><span data-stu-id="0abba-428">If the recognizer is running, applications must use <xref:System.Speech.Recognition.SpeechRecognitionEngine.RequestRecognizerUpdate%2A> to pause the speech recognition engine before loading, unloading,  enabling, or disabling a grammar.</span></span>  

 <span data-ttu-id="0abba-429">При создании делегата <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> необходимо указать метод, обрабатывающий событие.</span><span class="sxs-lookup"><span data-stu-id="0abba-429">When you create a <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> delegate, you identify the method that will handle the event.</span></span> <span data-ttu-id="0abba-430">Чтобы связать событие с обработчиком событий, нужно добавить в событие экземпляр делегата.</span><span class="sxs-lookup"><span data-stu-id="0abba-430">To associate the event with your event handler, add an instance of the delegate to the event.</span></span> <span data-ttu-id="0abba-431">Обработчик событий вызывается всякий раз, когда происходит событие, если делегат не удален.</span><span class="sxs-lookup"><span data-stu-id="0abba-431">The event handler is called whenever the event occurs, unless you remove the delegate.</span></span> <span data-ttu-id="0abba-432">Дополнительные сведения о делегатах-обработчиках событий см. в разделе [события и делегаты](http://go.microsoft.com/fwlink/?LinkId=162418).</span><span class="sxs-lookup"><span data-stu-id="0abba-432">For more information about event-handler delegates, see [Events and Delegates](http://go.microsoft.com/fwlink/?LinkId=162418).</span></span>  



## Examples  
 <span data-ttu-id="0abba-433">Следующий пример создает распознавания речи в процесс и затем создает два вида грамматик, распознавая конкретных слов и принятие свободного диктовки.</span><span class="sxs-lookup"><span data-stu-id="0abba-433">The following example creates an in-process speech recognizer, and then creates two types of grammars for recognizing specific words and for accepting free dictation.</span></span> <span data-ttu-id="0abba-434">В примере создается <xref:System.Speech.Recognition.Grammar> объекта из всех грамматик распознавания речи завершенного асинхронно загружает <xref:System.Speech.Recognition.Grammar> объектов <xref:System.Speech.Recognition.SpeechRecognitionEngine> экземпляра.</span><span class="sxs-lookup"><span data-stu-id="0abba-434">The example constructs a <xref:System.Speech.Recognition.Grammar> object from each of the completed speech recognition grammars, then asynchronously loads the <xref:System.Speech.Recognition.Grammar> objects to the <xref:System.Speech.Recognition.SpeechRecognitionEngine> instance.</span></span> <span data-ttu-id="0abba-435">Обработчики для распознавания <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> и <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> события записи на консоль имя <xref:System.Speech.Recognition.Grammar> объект, который был использован для выполнения распознавания и текст результата распознавания, соответственно.</span><span class="sxs-lookup"><span data-stu-id="0abba-435">Handlers for the recognizer's <xref:System.Speech.Recognition.SpeechRecognitionEngine.LoadGrammarCompleted> and <xref:System.Speech.Recognition.SpeechRecognitionEngine.SpeechRecognized> events write to the console the name of the <xref:System.Speech.Recognition.Grammar> object that was used to perform the recognition and the text of the recognition result, respectively.</span></span>  

```  
using System;  
using System.Speech.Recognition;  

namespace SampleRecognition  
{  
  class Program  
  {  
    private static SpeechRecognitionEngine recognizer;  
    public static void Main(string[] args)  
    {  

      // Initialize an in-process speech recognition engine and set its input.  
      recognizer = new SpeechRecognitionEngine();  
      recognizer.SetInputToDefaultAudioDevice();  

      // Add a handler for the LoadGrammarCompleted event.  
      recognizer.LoadGrammarCompleted +=  
        new EventHandler<LoadGrammarCompletedEventArgs>(recognizer_LoadGrammarCompleted);  

      // Add a handler for the SpeechRecognized event.  
      recognizer.SpeechRecognized +=  
        new EventHandler<SpeechRecognizedEventArgs>(recognizer_SpeechRecognized);  

      // Create the "yesno" grammar.  
      Choices yesChoices = new Choices(new string[] { "yes", "yup", "yeah" });  
      SemanticResultValue yesValue =  
          new SemanticResultValue(yesChoices, (bool)true);  
      Choices noChoices = new Choices(new string[] { "no", "nope", "neah" });  
      SemanticResultValue noValue =  
          new SemanticResultValue(noChoices, (bool)false);  
      SemanticResultKey yesNoKey =  
          new SemanticResultKey("yesno", new Choices(new GrammarBuilder[] { yesValue, noValue }));  
      Grammar yesnoGrammar = new Grammar(yesNoKey);  
      yesnoGrammar.Name = "yesNo";  

      // Create the "done" grammar.  
      Grammar doneGrammar =  
        new Grammar(new Choices(new string[] { "done", "exit", "quit", "stop" }));  
      doneGrammar.Name = "Done";  

      // Create a dictation grammar.  
      Grammar dictation = new DictationGrammar();  
      dictation.Name = "Dictation";  

      // Load grammars to the recognizer.  
      recognizer.LoadGrammarAsync(yesnoGrammar);  
      recognizer.LoadGrammarAsync(doneGrammar);  
      recognizer.LoadGrammarAsync(dictation);  

      // Start asynchronous, continuous recognition.  
      recognizer.RecognizeAsync(RecognizeMode.Multiple);  

      // Keep the console window open.  
      Console.ReadLine();  
    }  

    // Handle the LoadGrammarCompleted event.   
    static void recognizer_LoadGrammarCompleted(object sender, LoadGrammarCompletedEventArgs e)  
    {  
      string grammarName = e.Grammar.Name;  
      bool grammarLoaded = e.Grammar.Loaded;  

      if (e.Error != null)  
      {  
        Console.WriteLine("LoadGrammar for {0} failed with a {1}.",  
        grammarName, e.Error.GetType().Name);  

        // Add exception handling code here.  
      }  

      Console.WriteLine("Grammar {0} {1} loaded.",  
      grammarName, (grammarLoaded) ? "is" : "is not");  
    }  

    // Handle the SpeechRecognized event.  
    static void recognizer_SpeechRecognized(object sender, SpeechRecognizedEventArgs e)  
    {  
      Console.WriteLine("Grammar({0}): {1}", e.Result.Grammar.Name, e.Result.Text);  

      // Add event handler code here.  
    }  
  }  
}  

```



## Remarks  
 <span data-ttu-id="0abba-444">Распознаватель параметров может содержать строку, 64-разрядное целое число или данных адресов памяти.</span><span class="sxs-lookup"><span data-stu-id="0abba-444">Recognizer settings can contain string, 64-bit integer, or memory address data.</span></span> <span data-ttu-id="0abba-445">В следующей таблице описаны параметры, которые определены для API речи (SAPI)-совместимые распознавателя.</span><span class="sxs-lookup"><span data-stu-id="0abba-445">The following table describes the settings that are defined for a Microsoft Speech API (SAPI)-compliant recognizer.</span></span> <span data-ttu-id="0abba-446">Следующие параметры должны иметь такой же диапазон для каждого распознаватель, который поддерживает параметр.</span><span class="sxs-lookup"><span data-stu-id="0abba-446">The following settings must have the same range for each recognizer that supports the setting.</span></span> <span data-ttu-id="0abba-447">Совместимые SAPI распознаватель не требуется для поддержки этих параметров и может поддерживать другие параметры.</span><span class="sxs-lookup"><span data-stu-id="0abba-447">A SAPI-compliant recognizer is not required to support these settings and can support other settings.</span></span>  

|<span data-ttu-id="0abba-448">name</span><span class="sxs-lookup"><span data-stu-id="0abba-448">Name</span></span>|<span data-ttu-id="0abba-449">Описание:</span><span class="sxs-lookup"><span data-stu-id="0abba-449">Description</span></span>|  
